/** This script is executed before the Record is generated
* `current`- GlideRecord produced by Record Producer
* Don't use `current.update()` or `current.insert()` as the record is generated by Record Producer
* Don't use `current.setValue('sys_class_name', 'xxx')` as this will trigger reparent flow and can cause data loss
* Avoid `current.setAbortAction()` and generate a separate record
* Use `producer.var1` to access variables
*/
var x = new GlideRecord('x_852107_enterpr_0_governance_item');
x.addQuery('sys_id', producer.number);
x.query();
if (x.next()) {
    x.u_choice_1 = producer.u_choice_1;
    x.u_choice_19 = producer.u_choice_19;
    x.u_string_full_utf8_21 = producer.u_string_full_utf8_21;
	x.u_reference_20 =  gs.getUserDisplayName();
	if(producer.u_choice_1 == "architecturally significant to non-architecturally significant"){
		x.setValue('u_choice_13', 'non architecturally significant');
		x.setValue('u_choice_4', 'asa closed (not architecturally significant)');
		x.u_reference_14 = gs.getUserDisplayName();
	}
	else if(producer.u_choice_1 == "non-architecturally significant to architecturally significant"){
		x.setValue('u_choice_13', 'architecturally significant');
		x.setValue('u_choice_4', 'asa completed ( architecturally significant)');
		x.u_reference_2=gs.getUserID();

	}
	else if(producer.u_choice_1 == "no override"){
		x.setValue('');
	}
